function Atr_GetBonding (itemID, vpstr)

	if (itemID == nil) then
		return 0;
	end

	-- temp patch for feasts
	if (itemID == 43015 or itemID == 34753 or itemID == 43480 or itemID == 43478) then
		return 0;
	end

	-- temp patch for chaos orbs
	if (itemID == 52078) then
		return 0;
	end

	if (vpstr == nil) then
		vpstr = gAtr_MI;
	end

	local bonding = 0;		-- doesn't bind

	local x = itemID;
	
	if (x <= string.len (vpstr)) then

		local s = string.sub (vpstr, x, x);

		if (s ~= ".") then
			bonding = tonumber (s)
		end

--		zc.md (itemID, bonding, string.sub (vpstr, x-10, x+10))
--		bonding = zc.dec64 (s);
	end

	return bonding;
end



