Scripts.OnVerticalScroll:
local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(offset);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
if ( offset == 0 ) then
	_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollUpButton"]:Enable();
end
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end

local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(offset);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
if ( offset == 0 ) then
	_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollUpButton"]:Enable();
end
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end

Scripts.OnLoad:
VUHDO_repairStatusbar(self);
self:SetStatusBarTexture("Interface\\AddOns\\VuhDoOptions\\Images\\blue_lt_square_16_16");
self:SetStatusBarColor(1, 1, 1, 1);
self["SetProgress"] = function(self, aValue, aMaxValue)
	local tPercent = 100 * aValue / aMaxValue;
	self:SetValue(tPercent);
	_G[self:GetName() .. "Label"]:SetText(" " .. floor(tPercent) .. "%");
end
